mapper.select:
  	select 

	from t_electron_main
	where 1= 1

	  and t_agreement = #{tAgreement,jdbcType=INTEGER}


	  and  t_delete = #{tDelete,jdbcType=INTEGER}


	  and  t_register_id = #{tRegisterId,jdbcType=INTEGER}

   	GROUP BY t_id DESC
    limit #{start,jdbcType=INTEGER},#{size,jdbcType=INTEGER}

    select 
    count(t_id)
    from t_electron_main
    where 1= 1

	  and t_agreement = #{tAgreement,jdbcType=INTEGER}


	  and  t_delete = #{tDelete,jdbcType=INTEGER}


	  and  t_register_id = #{tRegisterId,jdbcType=INTEGER}

mapper.update:
update t_electron_main
t_agreement = #{tAgreement,jdbcType=INTEGER}, t_identity_card = #{tIdentityCard,jdbcType=VARCHAR}, t_identity_card_two = #{tIdentityCard2,jdbcType=VARCHAR}, t_business = #{tBusiness,jdbcType=VARCHAR}, t_sign = #{tSign,jdbcType=VARCHAR}
where t_id = #{tId,jdbcType=INTEGER}
